import { useState } from "react";
import PortfolioList from "../porfolioList/PortfolioList.jsx";
import "./portfolio.scss";

export default function Portfolio() {
    const [select, setSelected] = useState("featured");
    const list = [
        {
            id: "featured",
            title: "Featured",
        },
        {
            id: "web",
            title: "Web App",
        },
        {
            id: "mobile",
            title: "Mobile App",
        },
        {
            id: "design",
            title: "Design",
        },
        {
            id: "content",
            title: "Content",
        }
    ];
    return (
        <div className="portfolio" id="portfolio">
            <h1>Portfolio</h1>
            <ul>
                {
                    list.map((item)=>(
                        <PortfolioList title={item.title} active={select===item.id} setSelected={setSelected} id={item.id}/>
                    ))
                }
            </ul>
            <div className="container">
                <div className="item">
                    <img src="assets/b4.jpg" alt="Pic" />
                    <h3>Banking App</h3>
                </div>
                <div className="item">
                    <img src="assets/a1.png"  alt="Pic" />
                    <h3>Banking App</h3>
                </div>
                <div className="item">
                    <img src="assets/a3.png"  alt="Pic" />
                    <h3>Banking App</h3>
                </div>
                <div className="item">
                    <img src="assets/m1.png"  alt="Pic" />
                    <h3>Banking App</h3>
                </div>
                <div className="item">
                    <img src="assets/m2.png"  alt="Pic" />
                    <h3>Banking App</h3>
                </div>
                <div className="item">
                    <img src="assets/m3.png"  alt="Pic" />
                    <h3>Banking App</h3>
                </div>
                <div className="item">
                    <img src="assets/b3.jpg"  alt="Pic" />
                    <h3>Banking App</h3>
                </div>
                <div className="item">
                    <img src="assets/b5.jpg"  alt="Pic" />
                    <h3>Banking App</h3>
                </div>
            </div>
            
            


        </div>
    )
}
